import classNames from 'classnames';
import React, { useEffect, useState } from 'react';
import Year from '../../dashboard/Radios/year';
import Country from '../../dashboard/Radios/country';
import Genre from '../../dashboard/Radios/genre';
import Quality from '../../dashboard/Radios/quality';
import Rating from '../../dashboard/Radios/rating';
import Release from '../../dashboard/Radios/release';
import Type from '../../dashboard/Radios/type';
import Loader from "react-loader-spinner";
import FetchPaginateUpComingQuery from '../../hooks/pagenateUpcoming';
import FetchTrendsQuery from '../../hooks/getAllTrends';
import ReactPaginate from 'react-paginate';
import { Link } from 'react-router-dom';
import WatchMovie from '../watch/watchMovie';

export default function NewReleased() {
  const [activeDropdown, setActiveDropdown] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [pageCount, setpageCount] = useState(0)
  const [isHovering, setIsHovering] = useState(false);
  const [hoveredMovieId, setHoveredMovieId] = useState('');
  const { data, isLoading, isError:errorMessage } = FetchPaginateUpComingQuery(currentPage);
  const { data: movies } = FetchTrendsQuery();
  const moviesData = data?.data || [];
  const pageCounts = data?.pageCount || 0;

  const handlePageChange = (selectedPage:any) => {
    const pageNumber = selectedPage.selected + 1;
    console.log(pageNumber)
    setCurrentPage(pageNumber);
  };

  // const handleMouseOver = () => {
  //   setIsHovering(true);
  // };

  const handleMouseOut = () => {
    setIsHovering(false);
  };

  const handleMouseOver = (movieId: string) => {
    setHoveredMovieId(movieId);
  };

  const styleComponent = "mr-1 text-white bg-filterColor  h-[30px] hover:bg-changeColor focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-4 py-2.5 text-center inline-flex items-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800";
  // const buttonStyle = 'rounded bg-[#1a212d] ml-2 my-2 px-2 text-center text-[#605f62] h-[30px] font-light text-[12px]';

  const toggleDropdown = (dropdownName:any) => {
    if (activeDropdown === dropdownName) {
      setActiveDropdown('');
    } else {
      setActiveDropdown(dropdownName);
      console.log('activeDropdown is >>>', activeDropdown);
    }
  };

  return (
    <div className='bg-[#020916] pt-10'>
      <p>NEW RELEASE</p>
      <div className='flex flex-wrap'>
        <div className='w-3/4'>
          <div className='flex pl-2'>
            <div className=' h-[30px] '>
              <input
                type="text"
                placeholder='search...'
                className='text-[#605f62] bg-[#1a212d] w-[120px] rounded mr-2 h-[30px] text-center'
              />
            </div>
            <Year isOpen={activeDropdown === 'year'} onToggle={() => toggleDropdown('year')} style={styleComponent} />
            <Country isOpen={activeDropdown === 'country'} onToggle={() => toggleDropdown('country')} style={styleComponent} />
            <Genre isOpen={activeDropdown === 'genre'} onToggle={() => toggleDropdown('genre')} style={styleComponent} />
            <Quality isOpen={activeDropdown === 'quality'} onToggle={() => toggleDropdown('quality')} style={styleComponent} />
            <Rating isOpen={activeDropdown === 'rating'} onToggle={() => toggleDropdown('rating')} style={styleComponent} />
            <Release isOpen={activeDropdown === 'release'} onToggle={() => toggleDropdown('release')} style={styleComponent} />
            <Type isOpen={activeDropdown === 'type'} onToggle={() => toggleDropdown('type')} style={styleComponent} />
            <button
              data-dropdown-toggle="dropdownDefaultRadio"
              className="tex-black bg-[#ffae06] h-[30px] ml-1
              font-medium rounded-lg text-sm px-4 py-2.5 text-center inline-flex items-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800"
              type="button"
            >
              filter
              <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor" className="w-4 h-4 ml-4">
                <path strokeLinecap="round" strokeLinejoin="round" d="M16.023 9.348h4.992v-.001M2.985 19.644v-4.992m0 0h4.992m-4.993 0l3.181 3.183a8.25 8.25 0 0013.803-3.7M4.031 9.865a8.25 8.25 0 0113.803-3.7l3.181 3.182m0-4.991v4.99" />
              </svg>
            </button>
          </div>

          <div className="flex flex-wrap">
  {isLoading ? (
    'Loading...'
  ) : (
    <>
      {/* Page movie */}
      {moviesData && moviesData.map((movie: any) => (
        <div
          key={movie.id}
          // className="w-1/2 sm:w-1/3 md:w-1/4 lg:w-1/6 p-2"
          className={classNames}
          onMouseOver={() => handleMouseOver(movie.id)}
          onMouseOut={handleMouseOut}
        >
          <Link to={`/movie/${movie.id}`} className="hover:bg-radientColor">
            <img
              className="w-full h-auto bg-radientColor"
              src={
                hoveredMovieId === movie.id
                  ? `https://image.tmdb.org/t/p/w500/${movie.backdrop_path}`
                  : `https://image.tmdb.org/t/p/w500/${movie.poster_path}`
              }
              alt={movie.title}
            />
          </Link>
          <p className="pt-2 pl-2 truncate text-white">
            {movie.title || movie.name}
          </p>
          {hoveredMovieId === movie.id && (
            <div className="absolute mt-2">
              <WatchMovie movieId={movie.id} />
            </div>
          )}
        </div>
      ))}
    </>
  )}
</div>


          <div className="  mt-4 inline-flex -space-x-px">
          <ReactPaginate
            previousLabel={'Previous'}
            nextLabel={'Next'}
            breakLabel={'.....'}
            breakClassName='px-2 bg-white w-[45px] text-center'
            pageCount={pageCounts}
            marginPagesDisplayed={2}
            pageRangeDisplayed={5}
            onPageChange={handlePageChange}
            nextClassName = 'px-3 py-2 ml-4 leading-tight text-gray-500 bg-white border border-gray-300 rounded-r-lg hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white'
            containerClassName="bg-black inline-flex space-x-px  rounded ml-[500px] mb-4"
            activeClassName='bg-yellow-400 text-white'
            previousClassName="px-3 py-2 mr-0 leading-tight text-gray-500 bg-white border border-gray-300 rounded-l-lg hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white"
            pageClassName="px-3 py-2 pl-4 leading-tight text-gray-500 bg-white border border-gray-300 hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white"
            />
          </div>
        </div>

        <div className="w-1/4">
          <div className="h-full">
            {/* Place your text content here */}
            <div className='flex pt-[210px] pl-[5px] font-extrabold'>
              <p className='pl-4 text-white font-extrabold text-[24px]'>UPDATED</p>
              <div className=' ml-10 text-center pl-[70px]'>
                <button className=' w-[70px] text-center text-[#605f62] h-[30px] bg-[#1a212d] rounded-md flex pl-[9px] font-medium text-[12px] pt-[5px]'>
                  More
                  <span>
                    <svg xmlns="http://www.w3.org/2000/svg" height="28" viewBox="0 -960 960 960" width="28" className='pb-2 text-base font-semibold'>
                      <path d="m375-240-43-43 198-198-198-198 43-43 241 241-241 241Z" />
                    </svg>
                  </span>
                  <span className='bg-red-400'></span>
                </button>
              </div>
            </div>
            {/* The code on the update part */}
            <div className='pl-[5px] pt-[65px] '>
              {movies && movies.slice(0, 9).map((movie:any) => (
                <div className="flex-col w-[280px] h-[70px] mt-[2px] bg-[#020916] text-white  border-2 border-zinc-700 border-solid rounded-md ">
                  <div className='flex'>
                    <img src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`} alt="" className='w-10 h-[60px] md:32 pl-2 pt-2 rounded-md text-center' />
                    <p className='pt-2 pl-2 truncate'>{movie.title || movie.name}</p>
                  </div>
                </div>
              ))}

            </div>
          </div>
