import React, { useState } from 'react';
import FetchUpComingQuery from '../../hooks/getUpComing';
import Year from '../../dashboard/Radios/year';
import Country from '../../dashboard/Radios/country';
import Genre from '../../dashboard/Radios/genre';
import Quality from '../../dashboard/Radios/quality';
import Rating from '../../dashboard/Radios/rating';
import Release from '../../dashboard/Radios/release';
import Type from '../../dashboard/Radios/type';
import Loader from 'react-loader-spinner';

export default function NewReleased() {
  const { isLoading, isFetching, data } = FetchUpComingQuery();
  const [isOpen, setIsOpen] = useState(false);
  const [activeDropdown, setActiveDropdown] = useState('');

  const toggleDropdown = (dropdownName) => {
    if (activeDropdown === dropdownName) {
      setActiveDropdown('');
    } else {
      setActiveDropdown(dropdownName);
    }
  };

  const buttonStyle = 'rounded bg-[#1a212d] ml-2 my-2 px-2 text-center text-[#605f62] h-[30px] font-light text-[12px]';

  return (
    <div className="bg-[#020916] pt-10">
      <p>NEW RELEASE</p>

      <div className="flex flex-wrap">
        <div className="w-3/4">
          <div className="flex pl-2">
            <div className="h-[30px]">
              <input
                type="text"
                placeholder="search..."
                className="text-[#605f62] bg-[#1a212d] w-[120px] rounded mr-2 h-[30px] text-center"
              />
            </div>

            <Year isOpen={activeDropdown === 'year'} onToggle={() => toggleDropdown('year')} />
            <Country isOpen={activeDropdown === 'country'} onToggle={() => toggleDropdown('country')} />
            <Genre isOpen={activeDropdown === 'genre'} onToggle={() => toggleDropdown('genre')} />
            <Quality isOpen={activeDropdown === 'quality'} onToggle={() => toggleDropdown('quality')} />
            <Rating isOpen={activeDropdown === 'rating'} onToggle={() => toggleDropdown('rating')} />
            <Release isOpen={activeDropdown === 'release'} onToggle={() => toggleDropdown('release')} />
            <Type isOpen={activeDropdown === 'type'} onToggle={() => toggleDropdown('type')} />
            <button
              data-dropdown-toggle="dropdownDefaultRadio"
              className="text-white bg-blue-700 h-[30px] ml-2 font-medium rounded-lg text-sm px-4 py-2.5 text-center inline-flex items-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800"
              type="button"
            >
              filter
              <svg
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 24 24"
                stroke-width="1.5"
                stroke="currentColor"
                className="w-4 h-4 ml-4"
              >
                <path
                  stroke-linecap="round"
                  stroke-linejoin="round"
                  d="M16.023 9.348h4.992v-.001M2.985 19.644v-4.992m0 0h4.992m-4.993 0l3.181 3.183a8.25 8.25 0 0013.803-3.7M4.031 9.865a8.25 8.25 0 0113.803-3.7l3.181 3.182m0-4.991v4.99"
                />
              </svg>
            </button>
          </div>

          {/* Rest of your code */}
        </div>

        {/* Rest of your code */}
      </div>
    </div>
  );
}
