import React, { useState, useEffect } from 'react';
import {  Route, Routes } from 'react-router-dom';
import Home from '../pages/home';
import Profile from '../pages/profile';
// import VerifyEmail from '../firebase/verifyEmail';
import VerifyEmail from '../firebase/verfyEmail';
import auth from '../firebase/firebase';
import { onAuthStateChanged, User } from 'firebase/auth';
import { Navigate } from 'react-router-dom';
import { AuthProvider } from '../context/AuthContext';
import PrivateRoute from '../auth/PrivateRoute';
import Login from '../auth/login';
import Signup from '../auth/signup';
import NewReleased from '../pages/NewReleased/NewReleased';

export default function Allroutes() {
  const [currentUser, setCurrentUser] = useState<User | null>();
  const [timeActive, setTimeActive] = useState(null);

  useEffect(() => {
    onAuthStateChanged(auth, (user: User | null) => {
      setCurrentUser(user);
    });
  }, []);

  return (
    <>

    <AuthProvider value={{currentUser, timeActive, setTimeActive}}>
      <Routes>
      <Route path="/" element={<PrivateRoute> <Profile /> </PrivateRoute>} />
        <Route path="/login" element={
          !currentUser?.emailVerified
          ? <Login/>
          : <Navigate to='/' replace/>
        } />
        <Route path="/signup" element={
          !currentUser?.emailVerified 
          ? <Signup/>
          : <Navigate to='/' replace/>
        } />
        <Route path='/verify-email' element={<VerifyEmail/>} /> 
        <Route path='/news' element={<NewReleased/>}/>
      </Routes>  
    </AuthProvider>

</>
);
}
