import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Login from './login';
import { auth } from 'firebase/auth';
import { createUserWithEmailAndPassword, sendEmailVerification } from 'firebase/auth';
import { useAuthValue } from '../context/AuthContext';

export default function Signup() {
  const [isClick, setIsClick] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const { setTimeActive } = useAuthValue();

  const validatePassword = () => {
    let isValid = true;
    if (password !== '' && confirmPassword !== '') {
      if (password !== confirmPassword) {
        isValid = false;
        setError('Password does not match, try again');
      }
    }
    return isValid;
  };

  const HandleEmail = (e: React.FormEvent<HTMLInputElement>) => {
    const target = e.target as HTMLInputElement;
    setEmail(target.value);
  };

  const HandlePassword = (e: React.FormEvent<HTMLInputElement>) => {
    const target = e.target as HTMLInputElement;
    setPassword(target.value);
  };

  const HandleConfirmPassword = (e: React.FormEvent<HTMLInputElement>) => {
    const target = e.target as HTMLInputElement;
    setConfirmPassword(target.value);
  };

  const register = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setError('');
    if (validatePassword()) {
      // Create a new user with email and password using firebase
      createUserWithEmailAndPassword(auth, email, password)
        .then(() => {
          sendEmailVerification(auth.currentUser!)
            .then(() => {
              setTimeActive(true);
              navigate('/verify-email');
            })
            .catch((err) => alert(err.message));
        })
        .catch((err) => setError(err.message));
    }
    setEmail('');
    setPassword('');
    setConfirmPassword('');
  };

  const ToggleOnClick = () => {
    setIsClick(!isClick);
  };

  return (
    <>
      {isClick ? (
        <Login />
      ) : (
        <div className='bg-slate-300 h-screen'>
          <div className='w-full max-w-xs ml-[510px] pt-[150px] mb-0'>
            <form
              className='bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4 w-[450px]'
              onSubmit={register}
            >
              <div className='mb-4'>
                <label className='block text-gray-700 text-sm font-bold mb-2' htmlFor='email'>
                  email
                </label>
                <input
                  className='shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline'
                  id='email'
                  type='email'
                  placeholder='example@gmail.com'
                  onClick={HandleEmail}
                />
              </div>

              <div className='mb-4'>
                <label className='block text-gray-700 text-sm font-bold mb-2' htmlFor='password'>
                  password
                </label>
                <input
                  className='shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline'
                  id='password'
                  type='password'
                  placeholder='*************************'
                  onClick={HandlePassword}
                />
              </div>

              <div className='mb-4'>
                <label className='block text-gray-700 text-sm font-bold mb-2' htmlFor='confirmPassword'>
                  confirm password
                </label>
                <input
                  className='shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline'
                  id='confirmPassword'
                  type='password'
                  placeholder='*************************'
                  onClick={HandleConfirmPassword}
                />
              </div>
              <div className='flex items-center justify-between'>
                <button
                  className='bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline'
                  type='submit'
                >
                  Sign up
                </button>
                <a className='inline-block align-baseline font-bold text-sm text-blue-500 hover:text-blue-800' href='#'>
                  Forgot Password?
                </a>
              </div>
              <div className='py-2' onClick={ToggleOnClick}>
                <p>
                  Already have an account? <Link to='/login' className='text-blue-400'>Sign in</Link>
                </p>
              </div>
            </form>

            <p className='text-center text-gray-500 text-xs'>&copy;2020 Acme Corp. All rights reserved.</p>
          </div>
        </div>
      )}
    </>
  );
}
