import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import FetchSingleMovieDetails from '../../hooks/detailMovie';

export default function MovieDetails() {
  const { movieId } = useParams();

  const { data: movie, isLoading, isError, error } = FetchSingleMovieDetails(movieId);

  return (
    <>
      {movie && (
        <div className="flex items-center justify-center">
          <div className="w-full max-w-screen">
            <div className="relative">
              <img
                className="w-full h-[480px] object-cover object-center"
                src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`}
                alt="Card Image"

              />

              <div
                className="absolute bottom-0 left-0 w-full h-[300px] bg-gradient-to-t from-gray-900 to-transparent transform origin-bottom"
                style={{ boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)' }}
              ></div>
            </div>
            <div className="px-6 py-4 flex">
              <div className="mb-2 ml-4 bg-black w-auto rounded px-2 py-1">
                <input type="radio"/>
                <label htmlFor="okay" className='pl-1 text-white'>Auto Play</label>
              </div>

            </div>
          </div>
        </div>
      )}
    </>
  );
}
