import React,{useState} from 'react'

export interface CommentInputProps {
    onSubmit: (text:string) => void;
}

export const CommentInput: React.FC<CommentInputProps> = ({ onSubmit })=>{
  return (
  <form className='bg-gray-500 text-blue-300'>

<label htmlFor="message" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Your message</label>
<textarea id="message" rows="4" class="block p-2.5 w-full text-sm text-gray-900 bg-gray-50 rounded-lg border border-gray-300 focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500" placeholder="Write your thoughts here..."></textarea>

    <button className='bg-gray-400 '>Submit</button>
  </form>
  )
}
