const Profile = ()=>{

  const [movies, setMovies] = useState([])

  useEffect(()=>{
    FetchData()
  },[])


  const FetchData = async()=>{
    try{
      const uri = `https://api.themoviedb.org/3/trending/all/day?language=en-US`
        const options = {
          method: 'GET',
          headers: {
            accept: 'application/json',
            Authorization: 'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiJiYjMyYTNhOTgxMDQzNzc3YzUyNjE4YTRmNmZhZjc1ZCIsInN1YiI6IjY0NzE5NWYzOTQwOGVjMDBjMjhlMzNhNCIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.TFNE-KUDbxeeR7hY0vXNAx_Q_gTQBzJCanXUpw51Hmg'
          }
        };

        const dataToFetch = await fetch(uri,options)

        if(dataToFetch.ok){
          alert('your response was able to go through successfully')
        }

        if(!dataToFetch.ok){
          alert('check your data again at this point')
        }

        const response  = dataToFetch.json()
        return response


    }
    catch(err){
      console.log('the error message is given as: ', err)

    }

  }




  return (
    <>
    {
      movies.map((movie:any)=>{
        <div key={movie.id}>
          <p>{movie.title}</p>

        </div>
      })
    }

    </>
  )

}

export default Profile
