import { useQuery, useInfiniteQuery } from "react-query"

import axios from "axios";

const FetchPaginateUpComingQuery = () => {
  return useInfiniteQuery("movies", async ({ pageParam = 1 }) => {
    const uri = `https://api.themoviedb.org/3/movie/upcoming?language=en-US&page=${pageParam}`;
    const options = {
      method: "GET",
      headers: {
        accept: "application/json",
        Authorization: "Bearer YOUR_AUTH_TOKEN",
      },
    };

    const response = await axios(uri, options);
    if (response.status === 200) {
      console.log("The data is >>>", response.data.results);
      return response.data.results;
    }
    throw new Error("Error occurred");
  }, {
    getNextPageParam: (lastPage) => {
      const nextPage = lastPage.page + 1;
      return nextPage <= lastPage.total_pages ? nextPage : undefined;
    },
  });
};




export default FetchPaginateUpComingQuery
