import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import FetchSingleMovieDetails from '../../hooks/detailMovie';

export default function MovieDetails() {
  const { movieId } = useParams();

  const { data: movie, isLoading, isError, error } = FetchSingleMovieDetails(movieId);

  return (
    <div className="w-screen flex justify-center items-center">
      {movie && (
        <div className="relative">
          <img
            src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`}
            className="w-full h-auto"
            alt={movie.title}
          />
          <div className="absolute inset-0 bg-gradient-to-t from-transparent to-black"></div>
          <div className="absolute bottom-0 left-0 p-4">
            <p>{movie.title}</p>
            <p>{movie.overview}</p>
          </div>
        </div>
      )}
    </div>
  );
}
