import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import FetchSingleMovieDetails from '../../hooks/detailMovie';

export default function MovieDetails() {
  const { movieId } = useParams();

  const { data: movie, isLoading, isError, error } = FetchSingleMovieDetails(movieId);

  return (
    <>
      {movie && (
        <div className="flex items-center justify-center">
          <div className="w-full max-w-screen">
            <div className="relative">
              <img
                className="w-full h-[480px] object-cover"
                src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`}
                alt="Card Image"
              />
              <div
                className="absolute bottom-0 left-0 w-full h-[300px] bg-gradient-to-t from-black to-transparent transform origin-bottom"
                style={{ boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)' }}
              ></div>
            </div>
            <div className="px-6 py-4">
              <div className="font-bold text-xl mb-2">Card Title</div>
              <p className="text-gray-700 text-base">Card description goes here.</p>
            </div>
          </div>
        </div>
      )}
    </>
  );
}
